__NUXT_JSONP__("/php/regular-expressions-in-php", (function(a){return {data:[{article:{slug:"regular-expressions-in-php",description:"A regular expression is a sequence of characters that forms a search pattern. When you search for data in a text, you can use this search pattern to describe what you are searching for.\u003Cbr\u003E\u003Cbr\u003E\nA regular expression can be a single character, or a more complicated pattern.\nRegular expressions can be used to perform all types of text search and text replace operations.\u003Cbr\u003E\u003Cbr\u003E\n`preg_match()` Returns 1 if the pattern was found in the string and 0 if not",title:"Regular Expressions in PHP",date:"2021-06-11T17:21:00.000Z",image:"\u002Fimages\u002Fphp\u002Fregular-expressions-in-php.png",tags:["php","regular-expressions"],instagram:"https:\u002F\u002Fwww.instagram.com\u002Fp\u002FCP-yLWIDR8B\u002F?utm_source=ig_web_copy_link",summery:"A regular expression is a sequence of characters that forms a search pattern. When you search for data in a text, you can use this search pattern to describe what you are searching for.",toc:[],body:{type:"root",children:[]},dir:"\u002Fphp",path:"\u002Fphp\u002Fregular-expressions-in-php",extension:".md",createdAt:a,updatedAt:a}}],fetch:{},mutations:void 0}}("2023-01-15T15:56:12.370Z")));